h=\"catalog/view/theme/*/template/{product,information,account,affiliate,checkout,error,blog,gallery,module,extension/module,extension/module/productbundles,extension/quickcheckout,extension/module/oclayerednavigation}/*.tpl\" error=\"skip\">\r\n		<operation error=\"skip\">\r\n			<search index=\"0\"><![CDATA[class=\"breadcrumb]]></search>\r\n			<add position=\"replace\"><![CDATA[itemscope itemtype=\"http://schema.org/BreadcrumbList\" class=\"breadcrumb]]></add>\r\n		</operation>\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<?php foreach ($breadcrumbs as $breadcrumb) { ?>]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n			<?php $last_crumb = array_pop($breadcrumbs); $i=1; ?>\r\n			<?php foreach ($breadcrumbs as $breadcrumb) { ?>\r\n				<li itemprop=\"itemListElement\" itemscope itemtype=\"http://schema.org/ListItem\"><a href=\"<?php echo $breadcrumb[\'href\']; ?>\" itemprop=\"item\"><span itemprop=\"name\"><?php echo $breadcrumb[\'text\']; ?></span></a><meta itemprop=\"position\" content=\"<?php echo $i; ?>\" /></li>\r\n			<?php $i++; } ?>\r\n			<li itemprop=\"itemListElement\" itemscope itemtype=\"http://schema.org/ListItem\"><link href=\"<?php echo $last_crumb[\'href\']; ?>\" itemprop=\"item\"/><span itemprop=\"name\"><?php echo $last_crumb[\'text\']; ?></span><meta itemprop=\"position\" content=\"<?php echo $i; ?>\" /></li>\r\n			<?php if (2+2==5) { ?>\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	<file path=\"catalog/controller/product/product.php\">\r\n		<operation>\r\n			<search><![CDATA[\r\n			public function index() {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			protected function getPath($parent_id, $current_path = \'\') {\r\n				$category_info = $this->model_catalog_category->getCategory($parent_id);          \r\n				if ($category_info) {\r\n					if (!$current_path) {\r\n						$new_path = $category_info[\'category_id\'];\r\n					} else {\r\n						$new_path = $category_info[\'category_id\'] . \'_\' . $current_path;\r\n					}			  \r\n					$path = $this->getPath($category_info[\'parent_id\'], $new_path);			  \r\n					if ($path) {\r\n						return $path;\r\n					} else {\r\n						return $new_path;\r\n					}\r\n				}\r\n			}\r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\r\n			if ($product_info) {\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			if(!isset($category_info)) {\r\n				$categories = $this->model_catalog_product->getCategories($this->request->get[\'product_id\']);\r\n				if($categories) {				\r\n					foreach($categories as $category){			  \r\n						$path = $this->getPath($category[\'category_id\']);\r\n						$category_info = $this->model_catalog_category->getCategory($category[\'category_id\']);\r\n						if($path){\r\n							$cat_path = $path;\r\n						}else{\r\n							$cat_path = $category_info[\'category_id\'];\r\n						}				  \r\n						if($category_info) {\r\n							$path = \'\';\r\n							$cat_path = explode(\'_\', $cat_path);\r\n							foreach ( $cat_path as $path_id) {\r\n							  if (!$path) {\r\n								$path = $path_id;\r\n							  } else {\r\n								$path .= \'_\' . $path_id;\r\n							  }\r\n								  \r\n							  $category_info = $this->model_catalog_category->getCategory($path_id);\r\n							  \r\n							  if ($category_info) {\r\n								  $data[\'breadcrumbs\'][] = array(\r\n								  \'text\'      => $category_info[\'name\'],\r\n								  \'href\'      => $this->url->link(\'product/category\', \'&path=\' . $path),\r\n								  \'separator\' => $this->language->get(\'text_separator\')\r\n								  );\r\n								}\r\n							}\r\n						break;\r\n						}			  \r\n					}\r\n				}\r\n			}\r\n			]]></add>\r\n		</operation>\r\n	</file> \r\n</modification>',1,'2021-01-21 02:23:23'),(13,0,'2 simple url rewrite','2 simple url rewrite','deeman','2.0.0','http://simpleopencart.com','\n            <modification>\n                <name>2 simple url rewrite</name>\n                <code>2 simple url rewrite</code>\n            